import { useEffect, useState } from 'react';
import BookSlider from '../BookSlider';
import './styles.scss';
import { suggestedCategories, bookArchives } from './constants';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import {
	setBucket,
	setWishlist,
	setSuggestedBooks,
	setDumpedBooks,
	setPreviousBooks,
	setCurrentBooks,
} from '../../reducers/wishlistSlice';
import {setUser, setAlert} from '../../reducers/mainSlice';
import { getAgeGroupColor, getDay, getDate } from '../../utils';
import { MdClose } from 'react-icons/md';
import { FaCalendar } from 'react-icons/fa';
import axios from 'axios';
import devUrls from '../../utils/devUrls';

const YourLibrary = () => {
	const [ changingBucket, setChangingBucket ] = useState(false);
	const state = useSelector(state => state);
	const { main: { user }, wishlist: { bucket, wishlist, suggestedBooks } } = useSelector(state => state);
	const dispatch = useDispatch();

	const createBucket = async () => {
		try {
			await axios.get(devUrls.createBucket, {
				withCredentials: true,
			});
			getBucket();
		} catch (err) {
			if(err.response?.data.message)
				dispatch(setAlert({text: err.response.data.message, color: '#F75549'}))
		}
	};

	const placeOrder = async () => {
		try {
			await axios.get(devUrls.placeOrder, {
				withCredentials: true,
			});
			dispatch(setAlert({text: 'Order placed', color: '#33A200'}));
			getBucket();
			getCurrentBooks();
			getPreviousBooks();
		} catch (err) {
			if(err.response?.data.message)
				dispatch(setAlert({text: err.response.data.message, color: '#F75549'}))
		}
	};

	const removeBucket = async (book) => {
		try {
			await axios.post(devUrls.removeFromBucket,
			{guid: book.guid},
			{withCredentials: true});
			getBucket();
		} catch (err) {
			if(err.response?.data.message)
				dispatch(setAlert({text: err.response.data.message, color: '#F75549'}))
			console.log(err);
		}
		setChangingBucket(false);
	};

	const getBucket = async () => {
		try {
			const response = await axios.get(devUrls.getBucket, {
				withCredentials: true,
				params: { guid: user.guid },
			});
			dispatch(setBucket({ bucket: response.data.wishlists }));
		} catch (err) {
			console.log(err);
		}
	};

	const getWishlist = async () => {
		try {
			const response = await axios.get(devUrls.getWishlist, {
				withCredentials: true,
				params: { guid: user.guid },
			});
			dispatch(setWishlist({ wishlist: response.data.wishlists }));
		} catch (err) {
			console.log(err);
		}
	};

	const getCurrentBooks = async () => {
		try {
			const response = await axios.get(devUrls.getCurrentBooks, {
				withCredentials: true,
				params: { guid: user.guid },
			});
			dispatch(setCurrentBooks({ currentBooks: response.data.books }));
		} catch (err) {
			console.log(err);
		}
	};

	const getPreviousBooks = async () => {
		try {
			const response = await axios.get(devUrls.getPreviousBooks, {
				withCredentials: true,
				params: { guid: user.guid },
			});
			dispatch(setPreviousBooks({ previousBooks: response.data.books }));
		} catch (err) {
			console.log(err);
		}
	};

	const getDumpedBooks = async () => {
		try {
			const response = await axios.get(devUrls.getDumps, {
				withCredentials: true,
				params: { guid: user.guid },
			});
			dispatch(setDumpedBooks({ dumpedBooks: response.data.dumps }));
		} catch (err) {
			console.log(err);
		}
	};

	const getSuggestions = async () => {
		if (user.children?.length) {
			const newSuggestedBooks = [];
			for (const child of user.children) {
				try {
					const response = await axios.get(devUrls.getSuggestions, {
						withCredentials: true,
						params: { guid: child.guid },
					});
					newSuggestedBooks.push(response.data.suggestions);
				} catch (err) {
					console.log(err);
				}
			}
			dispatch(setSuggestedBooks({ suggestedBooks: newSuggestedBooks }));
		}
	};

	const changeDeliveryDate = async (event) => {
		try {
			await axios.post(devUrls.changeDeliveryDate, {
				delivery_date: event.target.value.toString(),
			}, {withCredentials: true});
			dispatch(setUser({user: {...user, next_delivery_date: event.target.value.toString()}}));
		} catch (err) {
			dispatch(setAlert({text: err.response.data.message, color: '#F75549'}))
			console.log(err);
		}
	};

	useEffect(() => {
		getWishlist();
	}, [bucket]);

	useEffect(
		() => {
			getSuggestions();
			getPreviousBooks();
			getBucket();
			getDumpedBooks();
			getCurrentBooks();
		},
		[]
	);

	return (
		<div className="your-library">
			<h3>Your Library</h3>
			<div className="bucket">
				<h3>Next Delivery Bucket</h3>
				{bucket.length
					?
					<div className="bucket-details">
						<div className="bucket-list">
							{bucket.map((book, i) => {
								return (
									<div className="bucket-book" key={i}>
										<img src={book.image} alt="Book" />
										<div
											onClick={() => removeBucket(book)}
											className={`bucket-book-overlay ${changingBucket ? 'show-overlay' : ''}`}
										>
											<MdClose />
											<p>Remove</p>
										</div>
									</div>
								);
							})}
						</div>
						<div className="bucket-actions">
							<button onClick={() => setChangingBucket(_ => !_)}>
								{changingBucket ? 'Confirm' : 'Change'}
							</button>
							<button onClick={placeOrder}>Place Order</button>
						</div>
					</div>
					:
					<button className='gold-button create-bucket' onClick={createBucket}>Create Bucket</button>
				}
				<button className="date-button">
					<input type="date" onChange={changeDeliveryDate}/>
					<span>Delivery Date{user.next_delivery_date && ` - ${getDate(user.next_delivery_date)}`}</span>
				</button>
				{user.next_delivery_date && <div className="time-date">
					<div className="time-date-column">
						<img src="/icons/time.png" alt="Time" />
						<p>Time - 10AM to 6PM</p>
					</div>
					<div className="time-date-column">
						<FaCalendar />
						<p>Day - {getDay(user.next_delivery_date)}</p>
					</div>
				</div>}
			</div>
			<div className="wishlist">
				<h3>Wishlist</h3>
				{wishlist.length > 0
					? <BookSlider books={wishlist} showTags={false} overlay="wishlist" />
					: <div className="empty-wishlist">
							<h2><span>Like Books</span> to add<br />them in your wishlist</h2>
							<img src="/icons/wishlist.png" alt="Wishlist" />
						</div>}
			</div>
			{user.children?.map((child, i) => {
				return (
					<div className="suggestions" key={i}>
						<BookSlider
							books={suggestedBooks.length ? suggestedBooks[i] : []}
							color={getAgeGroupColor(child.dob)}
							categories={suggestedCategories}
							title={`Suggested for ${child.name}`}
							overlay="suggested"
						/>
					</div>
				);
			})}
			<div className="archives">
				{bookArchives.map((archive, i) => {
					return (
						<div className="archive" key={i}>
							<h3>{archive.title}</h3>
							{state.wishlist[archive.name]?.length
								? <BookSlider
										books={state.wishlist[archive.name]}
										bookBackground="#F1F1F0"
										showTags={false}
										overlay={archive.overlay}
									/>
								: <div className="archive-empty">
										{archive.emptyText}
										<img src={archive.emptyImage} alt="Archive" />
									</div>}
						</div>
					);
				})}
			</div>
			<div className="wish-more-books">
				<h2>Wish to see more Books!!!</h2>
				<Link to="/browse-library" className="gold-button">Browse Library</Link>
			</div>
		</div>
	);
};

export default YourLibrary;
