import {useState, useEffect, useRef} from 'react';
import ScrollContainer from 'react-indiana-drag-scroll'
import './styles.scss';
import {useSelector, useDispatch} from 'react-redux';
import {AiOutlineLeft, AiOutlineRight} from 'react-icons/ai';
import Loading from '../Loading';
import { LazyLoadImage } from 'react-lazy-load-image-component';
import {getOverlay} from './utils';
import Skeleton from 'react-loading-skeleton'
import 'react-loading-skeleton/dist/skeleton.css'

const BookSlider = ({
		title,
		categories,
		books,
		booksTitle,
		color,
		bookBackground,
		overlay,
		imageUrl,
		imageExtension='.jpg',
		getBooks = () => {},
		showTags = true,
		showOverlay = true
}) => {
	const [booksLoading, setBooksLoading] = useState(false);
	const [selectedCategory, setSelectedCategory] = useState('');
	const [sectionBooks, setSectionBooks] = useState(books.length ? books : ['No books to show']);
	const [booksScroll, setBooksScroll] = useState(0);
	const [categoriesScroll, setCategoriesScroll] = useState(0);
	const {main: {plan}} = useSelector(state => state);
	const dispatch = useDispatch();
	const booksRef = useRef(null);
	const categoriesRef = useRef(null);
	const tagsRef = useRef(null);

	const updateBooks = async () => {
		const element = booksRef.current.container.current;
		setBooksScroll(element.scrollLeft);
		if(!booksLoading && element.scrollLeft === element.scrollWidth - element.clientWidth) {
			setBooksLoading(true);
			await getBooks({
				start: element.children[0].children.length + 10,
				end: element.children[0].children.length + 20,
				guid: selectedCategory,
				type: 'a'
			});
			setBooksLoading(false);
		}
	};

	const slide = (direction, ref, update) => {
		const element = ref.current.container.current;
		setBooksScroll(element.scrollLeft)
		if(direction === 'right')
			element.scrollTo(element.scrollLeft + element.clientWidth - 100, 0);
		else
			element.scrollTo(element.scrollLeft - element.clientWidth - 100, 0);
		if(update)
			updateBooks();
	};

	useEffect(() => {
		if(books.length)
			setSectionBooks(books);
		else
			setSectionBooks(['No books to show']);
	}, [books]);

	useEffect(() => {
		if(categories?.length)
			setSelectedCategory(categories[0].guid);
	}, [categories]);

	useEffect(() => {
		const children = [];
		if(tagsRef.current) {
			for(let i = 2; i < tagsRef.current.children.length; ++i)
				children.push(tagsRef.current.children[i]);
			for(const child of children) {
				try {
					if(child)
						tagsRef.current.removeChild(child);
				} catch {}
			}
		}
	}, [tagsRef?.current]);

	useEffect(() => {
		if(booksRef?.current)
			booksRef.current.container.current.addEventListener('scroll', updateBooks);
		if(categoriesRef?.current) {
			categoriesRef.current?.container.current.addEventListener(
				'scroll',
				() => setCategoriesScroll(categories.current?.container.current.scrollLeft)
			);
		}
	}, [booksRef]);

	return (
		<div className="book-slider">
			{title && <h3 className='title'>{title}</h3>}
			{categories &&
					<div className="categories">
					{categoriesScroll !== 0 && <AiOutlineLeft className='left-arrow' onClick={() => slide('left', categoriesRef)}/>}
					{categoriesRef?.current?.container.current.scrollWidth > categoriesRef?.current?.container.current.clientWidth &&
						 <AiOutlineRight className='right-arrow' onClick={() => slide('right', categoriesRef)}/>
					 }
					<ScrollContainer vertical={false} ref={categoriesRef}>
						<div className='category-list'>
						{categories.map((category, i) => {
							return (
								<div className="category" key={i} onClick={() => {
										setSelectedCategory(category.guid);
										getBooks({guid: category.guid});
									}}>
									<div className='category-image'
										style={
											category.guid && category.guid === selectedCategory ?
											{border: '3px solid #F18B53'} :
											{}
										}
									>
										<img
											alt='Category'
											src={imageUrl ? `${imageUrl}/${category.name}${imageExtension}` : category.image}

										/>
									</div>
									<p>{category.name}</p>
								</div>
							);
						})}
						</div>
						</ScrollContainer>
					</div>
			}
			<div className="books" style={{ backgroundColor: color }}>
				{sectionBooks?.length && sectionBooks[0] !== 'No books to show' && booksScroll !== 0 &&
					<AiOutlineLeft
						className='left-arrow'
						onClick={() => slide('left', booksRef)}
						style={{color: color ? '#FFFFFF' : '#000000'}}
					/>
				}
				{(sectionBooks?.length && sectionBooks[0] !== 'No books to show') && booksRef?.current?.container.current.scrollWidth > booksRef?.current?.container.current.clientWidth &&
					<AiOutlineRight
						className='right-arrow'
						onClick={() => slide('right', booksRef, true)}
						style={{color: color ? '#FFFFFF' : '#000000'}}
					/>
				}
				{booksTitle && <h3 style={{color: color ? '#FFFFFF' : '#000000'}}>{booksTitle}</h3>}
				<ScrollContainer vertical={false} ref={booksRef}>
							{(!sectionBooks?.length || sectionBooks[0] === 'No books to show')
								?
								<h3 className='no-books-text'>No books to show</h3>
								:
								<div className="book-list">
									{sectionBooks?.map((book, i) => {
										return (
											<div className="book" style={{backgroundColor: bookBackground, height: 'auto'}} key={i}>
												{book === 'skeleton'
													?
													<Skeleton height={'12rem'}/>
													:
													<LazyLoadImage
													alt='Book'
													src={book.image}
													width={125}
													visibleByDefault={true}
												/>}
												{showTags &&
												<div className="book-badges" ref={tagsRef}>
													<img alt='Tag' src={`/images/Tags/${book.book_format}.png`} onError={(event) => event.target.style.display = 'none'} title={book.book_format}/>
													{book.categories?.slice(2).map(category => {
														return (
															<img alt='Tag' src={`/images/Tags/${category}.png`} onError={(event) => event.target.style.display = 'none'} title={category} key={category}/>
														);
													})}
													{booksTitle === 'Most Borrowed Books' && <img alt='Book' src='/images/Tags/Most borrowed.png' title='Most Borrowed'/>}
													{booksTitle === 'Amazon Bestsellers' && <img alt="Book" src='/images/Tags/Amazon best seller.png' title='Amazon Bestseller'/>}
												</div>}
												{showOverlay && getOverlay(overlay, sectionBooks, book, i, dispatch, plan)}
											</div>
										);
									})}
									{booksLoading && <Loading/>}
								</div>
							}
				</ScrollContainer>
			</div>
		</div>
	);
};

export default BookSlider;
