import { useEffect, useState } from 'react';
import './styles.scss';
import Plans from '../Plans';
import ScrollContainer from 'react-indiana-drag-scroll';
import BookSlider from '../BookSlider';
import { ageGroups } from './constants';
import { useDispatch, useSelector } from 'react-redux';
import { addBooks, setSeries, setAuthors, setBooks, setPublishers, setTypes } from '../../reducers/bookSlice';
import axios from 'axios';
import urls from '../../utils/urls';

const MAX = 10;

const BrowseLibrary = () => {
	const dispatch = useDispatch();
	const [ age, setAge ] = useState(1);
	const { book: { books, authors, series, publishers, types } } = useSelector(state => state);

	const updateBooks = (category, newBooks, type) => {
		if (type === 'a') dispatch(addBooks({ category, books: newBooks }));
		else dispatch(setBooks({ category, books: newBooks }));
	};

	const getMostBorrowedBooks = async ({ start = 0, end = 10, type = 'w' } = {}) => {
		try {
			const response = await axios.get(urls.getMostBorrowedBooks, { params: { age, start: start + 10 * (age - 1), end: (start + 10) + 10 * (age - 1)} });
			updateBooks('mostBorrowed', response.data.data, type);
		} catch (err) {
		}
	};

	const getAmazonBestsellerBooks = async ({ start = 0, end = 10, type = 'w' } = {}) => {
		try {
			const response = await axios.get(urls.getAmazonBestsellersBooks, { params: { age, start, end } });
			updateBooks('amazonBestsellers', response.data.data, type);
		} catch (err) {
		}
	};

	const getAuthorBooks = async ({ guid = 0, start = 0, end = 10, type = 'w' } = {}) => {
		try {
			const response = await axios.get(urls.getAuthorBooks, { params: { guid, start, end } });
			updateBooks('authorBooks', response.data.data, type);
		} catch (err) {
		}
	};

	const getSeriesBooks = async ({ guid = 0, start = 0, end = 10, type = 'w' } = {}) => {
		try {
			const response = await axios.get(urls.getSeriesBooks, { params: { guid, start, end } });
			updateBooks('seriesBooks', response.data.data, type);
		} catch (err) {
		}
	};

	const getPublisherBooks = async ({ guid = 0, start = 0, end = 10, type = 'w' } = {}) => {
		try {
			const response = await axios.get(urls.getPublisherBooks, { params: { guid, start, end } });
			updateBooks('publisherBooks', response.data.data, type);
		} catch (err) {
		}
	};

	const getTypeBooks = async ({ guid = 0, start = 0, end = 10, type = 'w' } = {}) => {
		try {
			const response = await axios.get(urls.getTypeBooks, { params: { guid, start, end } });
			updateBooks('typeBooks', response.data.data, type);
		} catch (err) {
		}
	};

	const getAuthors = async () => {
		const response = await axios.get(urls.getAuthors, { params: { age, start: 0, end: MAX } });
		dispatch(setAuthors({ authors: response.data.data }));
	};

	const getSeries = async () => {
		const response = await axios.get(urls.getSeries, { params: { age, start: 0, end: MAX } });
		dispatch(setSeries({ series: response.data.data }));
	};

	const getPublishers = async () => {
		const response = await axios.get(urls.getPublishers, { params: { age, start: 0, end: MAX } });
		dispatch(setPublishers({ publishers: response.data.data }));
	};

	const getTypes = async () => {
		const response = await axios.get(urls.getTypes, { params: { age, start: 0, end: MAX } });
		dispatch(setTypes({ types: response.data.data }));
	};

	useEffect(
		() => {
			if (authors.length) getAuthorBooks({ guid: authors[0].guid });
		},
		[ authors ]
	);

	useEffect(
		() => {
			if (series.length) getSeriesBooks({ guid: series[0].guid });
		},
		[ series ]
	);

	useEffect(
		() => {
			if (publishers.length) getPublisherBooks({ guid: publishers[0].guid });
		},
		[ publishers ]
	);

	useEffect(
		() => {
			if (types.length) getTypeBooks({ guid: types[0].guid });
		},
		[ types ]
	);

	useEffect(
		() => {
			getMostBorrowedBooks();
			getAmazonBestsellerBooks();
		},
		[ age ]
	);

	useEffect(
		() => {
			getAuthors();
			getSeries();
			getPublishers();
			getTypes();
		},
		[age]
	);

	return (
		<div className="browse-library">
			<div className="age-groups">
				<h2>Select By Age</h2>
				<ScrollContainer vertical={false}>
					<div className="age-group-list">
						{ageGroups.map((ageGroup, i) => {
							return (
								<div
									className="age-group"
									key={ageGroup.id}
									style={{
										border: `3px solid ${ageGroup.color}`,
										backgroundColor: i + 1 === age ? ageGroup.color : 'transparent',
										color: i + 1 === age ? '#FFFFFF' : '#000000',
									}}
									onClick={() => {
										i + 1 === age ? setAge(0) : setAge(i + 1);
									}}
								>
									<h2>{ageGroup.age}</h2>
									<p>Years</p>
									<img alt="Book" src={`./images/Art/${ageGroup.age}.png`} />
								</div>
							);
						})}
					</div>
				</ScrollContainer>
			</div>
			<BookSlider
				books={books.mostBorrowed}
				booksTitle="Most Borrowed Books"
				color={ageGroups[age === 0 ? 0 : (age - 1) % ageGroups.length].color}
			/>
			<BookSlider
				title="Type of Books"
				categories={types}
				books={books.typeBooks}
				color={ageGroups[age === 0 ? 1 : age % ageGroups.length].color}
				imageUrl="/images/Type of Books"
				imageExtension='.png'
				getBooks={getTypeBooks}
			/>
			<BookSlider
				title="Authors"
				categories={authors}
				books={books.authorBooks}
				color={ageGroups[age === 0 ? 2 : (age + 1) % ageGroups.length].color}
				imageUrl="/images/AllAuthors"
				getBooks={getAuthorBooks}
			/>
			<BookSlider
				title="Publishers"
				categories={publishers}
				books={books.publisherBooks}
				color={ageGroups[age === 0 ? 3 : (age + 2) % ageGroups.length].color}
				imageUrl="/images/AllPublishers"
				getBooks={getPublisherBooks}
			/>
			<BookSlider
				title="Series"
				categories={series}
				books={books.seriesBooks}
				color={ageGroups[age === 0 ? 4 : (age + 3) % ageGroups.length].color}
				imageUrl="/images/AllSeries"
				getBooks={getSeriesBooks}
			/>
			<BookSlider
				books={books.amazonBestsellers}
				booksTitle="Amazon Bestsellers"
				color={ageGroups[age === 0 ? 5 : (age + 4) % ageGroups.length].color}
				getBooks={getAmazonBestsellerBooks}
			/>
			<Plans />
		</div>
	);
};

export default BrowseLibrary;
