import { Fragment } from 'react';
import Plans from '../../Plans';
import { useDispatch, useSelector } from 'react-redux';
import { setAlert, setRegisterDetails } from '../../../reducers/mainSlice';
import './styles.scss';
import { AiFillCaretRight } from 'react-icons/ai';
import axios from 'axios';
import devUrls from '../../../utils/devUrls';

const PlanDetails = () => {
	const dispatch = useDispatch();
	const { registerDetails: { mobileNumber, selectedPlan, selectedSubscription, showSubscription, showPlans } } = useSelector(
		state => state.main
	);

	const selectPlan = async event => {
		event.preventDefault();
		if (!selectedPlan.planId) return dispatch(setAlert({ text: 'Please select a plan', color: '#F75549' }));
		try {
			await axios.post(devUrls.choosePlan, { plan: selectedPlan.planId, mobile_number: mobileNumber }, {
				withCredentials: true,
			});
			dispatch(setRegisterDetails({ showPlans: true }));
		} catch (err) {
			console.log(err);
		}
	};

	const changePlan = async event => {
		event.preventDefault();
		try {
			await axios.post(devUrls.changePlan, {mobile_number: mobileNumber}, { withCredentials: true });
			dispatch(
				setRegisterDetails({ showPlans: false, selectedPlan: {}, showSubscription: false, selectedSubscription: null })
			);
		} catch (err) {
			console.log(err);
		}
	};

	const selectSubscription = async event => {
		event.preventDefault();
		if (!selectedSubscription) return dispatch(setAlert({ text: 'Please select a subscription', color: '#F75549' }));
		try {
			dispatch(setRegisterDetails({ showSubscription: true }));
		} catch (err) {
			console.log(err);
		}
	};

	const initiatePayment = async event => {
		event.preventDefault();
		try {
			let response;
			if (selectedSubscription === 1)
				response = await axios.post(devUrls.generateSubscriptionId, {mobile_number: mobileNumber}, {
					withCredentials: true,
				});
			else
				response = await axios.post(devUrls.generateOrderId, {mobile_number: mobileNumber, card: selectedSubscription}, { withCredentials: true });
			const instance = window.Razorpay({
				...response.data,
				handler: async data => {
					if (selectedSubscription === 1) {
						await axios.post(
							devUrls.verifySubscription,
							{
								payment_id: data.razorpay_payment_id,
								subscription_id: data.razorpay_subscription_id,
								signature: data.razorpay_signature,
							},
							{ withCredentials: true }
						);
						await axios.post(
							devUrls.subscriptionSuccessful,
							{
								subscription_id: data.razorpay_subscription_id,
								payment_id: data.razorpay_payment_id,
								mobile_number: mobileNumber
							},
							{ withCredentials: true }
						);
					} else {
						await axios.post(
							devUrls.verifyOrder,
							{
								payment_id: data.razorpay_payment_id,
								order_id: data.razorpay_order_id,
								signature: data.razorpay_signature,
							},
							{ withCredentials: true }
						);
						await axios.post(
							devUrls.orderSuccessful,
							{
								order_id: data.razorpay_order_id,
								payment_id: data.razorpay_payment_id,
								mobile_number: mobileNumber
							},
							{ withCredentials: true }
						);
					}
					dispatch(setRegisterDetails({ paymentDone: true }));
				},
			});
			instance.on('payment.failed', response => {
				dispatch(setAlert({ text: 'Payment failed! Try again later', color: 'F75549' }));
			});
			instance.open();
		} catch (err) {
			console.log(err);
		}
	};

	if (!showSubscription && !showPlans) {
		return (
			<Fragment>
				<Plans select={true} />
				<button className="gold-button select-plan-button" onClick={selectPlan}>
					Next
				</button>
			</Fragment>
		);
	} else if (!showSubscription) {
		return (
			<div className="plan-details" style={{ width: 'min(700px, 95%)' }}>
				<h2>Selected Plan</h2>
				<div className="selected-plan" style={{ width: 'min(450px, 95%)' }}>
					<div style={{ backgroundColor: selectedPlan.borderColor }}>
						<h1>{selectedPlan.bookCount}</h1>
						<h3>Book/week</h3>
					</div>
					<div style={{ backgroundColor: selectedPlan.backgroundColor }}>
						<h3>Rs. {selectedPlan.price}</h3>
						<p>per month</p>
					</div>
					<img src={selectedPlan.image} alt="Plan" />
				</div>
				<div className="select-card-type">
					<p>Select Card Type</p>
					<div className="card-types">
						<div
							className="card-type"
							onClick={() => dispatch(setRegisterDetails({ selectedSubscription: 1 }))}
							style={selectedSubscription === 1 ? { backgroundColor: '#fccc91', border: '1px solid #fdb669' } : {}}
						>
							<img src="/icons/monthly.png" alt="Monthly" />
							<h3>
								Auto<br />Renewal
							</h3>
							<p>Monthly Subscription</p>
							<h1>1 month</h1>
						</div>
						<div
							className="card-type"
							onClick={() => dispatch(setRegisterDetails({ selectedSubscription: 3 }))}
							style={selectedSubscription === 3 ? { backgroundColor: '#fccc91', border: '1px solid #fdb669' } : {}}
						>
							<img src="/icons/quarter.png" alt="Quarterly" />
							<h3>
								Manual<br />Renewal
							</h3>
							<p>Quarterly Subscription</p>
							<h1>3 months</h1>
						</div>
						<div
							className="card-type"
							onClick={() => dispatch(setRegisterDetails({ selectedSubscription: 12 }))}
							style={selectedSubscription === 12 ? { backgroundColor: '#fccc91', border: '1px solid #fdb669' } : {}}
						>
							<img src="/icons/full.png" alt="Yearly" />
							<h3>
								Manual<br />Renewal
							</h3>
							<p>Yearly Subscription</p>
							<h1>1 Year</h1>
						</div>
					</div>
				</div>
				<button className="gold-button" onClick={selectSubscription} style={{ width: 'min(450px, 95%)' }}>
					Next
				</button>
				<button className="gold-border-button" onClick={changePlan} style={{ width: 'min(450px, 95%)' }}>
					Change Plan
				</button>
			</div>
		);
	}
	return (
		<div className="plan-details">
			<h2>{selectedSubscription} {selectedSubscription > 1 ? 'months' : 'month'} Subscription</h2>
			<div className="total-book-count" style={{ backgroundColor: selectedPlan.borderColor }}>
				<div>
					<h3>
						<span>{4 * selectedSubscription * selectedPlan.bookCount} Books</span>
						{' '}in{' '}
						{selectedSubscription}
						{' '}
						{selectedSubscription > 1 ? 'months' : 'month'}
					</h3>
					<p>{selectedPlan.bookCount} {selectedPlan.bookCount > 1 ? 'Books' : 'Book'} / week</p>
				</div>
				<img src={`/icons/${selectedPlan.bookCount}.png`} alt="Book" />
			</div>
			<div className="subscription-details">
				<div className="subscription-detail">
					<AiFillCaretRight style={{ color: selectedPlan.borderColor }} />
					<p>
						₹ {selectedPlan.price}
						{' '}per month{' '}
						{selectedSubscription !== 1 && `X ${selectedSubscription} = ₹ ${selectedPlan.price * selectedSubscription}`}
					</p>
				</div>
				{selectedSubscription === 12 &&
					<div className="subscription-detail">
						<AiFillCaretRight style={{ color: selectedPlan.borderColor }} />
						<p><b>20% Discount</b> - ₹ {Math.round(2.4 * selectedPlan.price)}</p>
					</div>}
				<div className="subscription-detail">
					<AiFillCaretRight style={{ color: selectedPlan.borderColor }} />
					<p><b>*Cancel Anytime*</b></p>
				</div>
			</div>
			<div className="total-amount">
				<h1>
					Amount - ₹{' '}
					{Math.round(selectedPlan.price * selectedSubscription - (selectedSubscription === 12 ? 2.4 * selectedPlan.price : 0))}
				</h1>
				<p>
					{selectedSubscription === 1
						? `Next month payment - ₹ ${selectedPlan.price * selectedSubscription}`
						: `Renew after ${selectedSubscription} months - ₹ ${Math.round(selectedPlan.price * selectedSubscription -
								(selectedSubscription === 12 ? 2.4 * selectedPlan.price : 0))}`}
				</p>
			</div>
			<button className="gold-button" onClick={initiatePayment} type="submit">
				Proceed to Payment
			</button>
			<button className="gold-border-button" onClick={changePlan}>
				Change Plan
			</button>
		</div>
	);
};

export default PlanDetails;
