import { useState, Fragment, useEffect } from 'react';
import { FaCheck } from 'react-icons/fa';
import ScrollContainer from 'react-indiana-drag-scroll';
import { useSelector } from 'react-redux';

export const OptionList = (
	{ options, imageUrl, title, imageFormat = 'png', showName = true, handleSelect, type, selectedChild, hoverName }
) => {
	const { childPreferences } = useSelector(state => state.main);
	const [showOptions, setShowOptions] = useState(Array(options.length).fill(true));

	const hideOption = (i) => {
		setShowOptions(_showOptions => {
			return _showOptions.map((showOption, j) => {
				if(i === j)
					return false;
				return showOption;
			})
		});
	};

	useEffect(() => {
		setShowOptions(Array(options.length).fill(true));
	}, [options]);

	if (!childPreferences.length) return <Fragment />;
	return (
		<div className="options-container">
			<p className="preference-title">{title}</p>
			<ScrollContainer className="options">
				{options.map((option, i) => {
					if(!showOptions[i])
						return <Fragment/>;
					return (
						<div
							className={`option ${!showName ? 'option-full' : ''}`}
							key={i}
							onClick={() => handleSelect(type, option)}
						>
							<div className='option-image'>
								<img src={`${imageUrl}/${option.name}.${imageFormat}`} alt="Option" onError={() => hideOption(i)}/>
							</div>
							{showName && <p>{option.name}</p>}
							{hoverName && <p className='hover-name'>{option.name}</p>}
							{(typeof childPreferences[selectedChild][type] === typeof []
								? childPreferences[selectedChild][type].includes(option.guid)
								: String(childPreferences[selectedChild][type]) === String(option.guid)) &&
								<div className="option-overlay">
									<div className="selected">
										<FaCheck />
									</div>
								</div>}
						</div>
					);
				})}
			</ScrollContainer>
		</div>
	);
};
