import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getApiAgeGroup, getAgeGroup, getAge, getAgeGroupColor } from '../../utils';
import './styles.scss';
import { numberOfBooks, readingLevels } from './constants';
import { OptionList } from './utils';
import Loading from '../Loading';
import { setAlert, initializePreferences, updatePreferences } from '../../reducers/mainSlice';
import axios from 'axios';
import devUrls from '../../utils/devUrls';

const Preferences = () => {
	const dispatch = useDispatch();
	const { user, childPreferences } = useSelector(state => state.main);
	const [ selectedChild, setSelectedChild ] = useState(0);
	const ageGroup = getAgeGroup(user.children[selectedChild].dob);
	const [types, setTypes] = useState([]);
	const [authors, setAuthors] = useState([]);
	const [series, setSeries] = useState([]);
	const [categories, setCategories] = useState([]);

	const submitPreferences = async (event) => {
		event.preventDefault();
		try {
			for(let i = 0; i < childPreferences.length; ++i) {
				await axios.post(devUrls.submitPreferences, {
					guid: childPreferences[i].guid,
					preference_data: {
						last_book_read1: childPreferences[i].lastBooks[0],
						last_book_read2: childPreferences[i].lastBooks[1],
						last_book_read3: childPreferences[i].lastBooks[2],
						books_read_per_week: childPreferences[i].numberOfBooksRead,
						reading_level: childPreferences[i].readingLevel,
						categories: childPreferences[i].categories,
						authors: childPreferences[i].authors,
						series: childPreferences[i].series,
						formats: childPreferences[i].types,
					}
				}, {withCredentials: true});
			}
			dispatch(setAlert({text: 'Preferences updated', color: '#33A200'}));
			window.location.reload();
		} catch (err) {
			console.log(err);
		}
	};

	const updateLastBook = ({ target: { value } }, i) => {
		const preference = {
			...childPreferences[selectedChild],
			lastBooks: childPreferences[selectedChild].lastBooks.map((lastBook, j) => {
				if (i === j) return value;
				return lastBook;
			}),
		};
		dispatch(updatePreferences({ i: selectedChild, preference }));
	};

	const updateSinglePreference = (type, value) => {
		const preference = { ...childPreferences[selectedChild] };
		preference[type] = value.guid;
		dispatch(updatePreferences({ i: selectedChild, preference }));
	};

	const updateMultiplePreferences = (type, value) => {
		const preference = { ...childPreferences[selectedChild] };
		preference[type] = [ ...preference[type] ];
		if(preference[type].includes(value.guid))
			preference[type] = preference[type].filter(v => v !== value.guid);
		else
			preference[type].push(value.guid);
		dispatch(updatePreferences({ i: selectedChild, preference }));
	};

	const getTypes = async () => {
		try {
			const response = await axios.get(devUrls.getTypes, {params: {
				age: getApiAgeGroup(user.children[selectedChild].dob), start: 0, end: 10
			}});
			setTypes(response.data.data);
		} catch (err) {
			console.log(err);
		}
	};

	const getAuthors = async () => {
		try {
			const response = await axios.get(devUrls.getAuthors, {params: {
				age: getApiAgeGroup(user.children[selectedChild].dob), start: 0, end: 10
			}});
			setAuthors(response.data.data);
		} catch (err) {
			console.log(err);
		}
	};

	const getSeries = async () => {
		try {
			const response = await axios.get(devUrls.getSeries, {params: {
				age: getApiAgeGroup(user.children[selectedChild].dob), start: 0, end: 10
			}});
			setSeries(response.data.data);
		} catch (err) {
			console.log(err);
		}
	};

	const getCategories = async () => {
		try {
			const response = await axios.get(devUrls.getGenres, {params: {
				age: getApiAgeGroup(user.children[selectedChild].dob)
			}});
			setCategories(response.data.data);
		} catch (err) {
			console.log(err);
		}
	};

	useEffect(() => {
		if(user.children) {
			getCategories();
			getTypes();
			getAuthors();
			getSeries();
		}
	}, [user, selectedChild]);

	useEffect(() => {
		if(user.children)
			dispatch(initializePreferences({children: user.children}));
	}, [user]);

	if (!user.children.length) return <Loading />;
	return (
		<div className="preferences">
			<h3 className="title">Tell us about yourself</h3>
			<div className="children">
				{user.children.map((child, i) => {
					return (
						<div
							className="child"
							style={
								i === selectedChild
									? { backgroundColor: getAgeGroupColor(child.dob) }
									: { borderColor: getAgeGroupColor(child.dob), color: getAgeGroupColor(child.dob) }
							}
							key={i}
							onClick={() => setSelectedChild(i)}
						>
							<h3>{child.name}</h3>
							<p>{getAge(child.dob)} years</p>
						</div>
					);
				})}
			</div>
			<div
				className="preferences-single"
				style={{ backgroundColor: getAgeGroupColor(user.children[selectedChild].dob) }}
			>
				<div className="last-books">
					<p className="preference-title">Last 3 books read</p>
					{childPreferences[selectedChild]?.lastBooks.map((lastBook, i) => {
						return (
							<div className="last-book" key={i}>
								<p>{i + 1}. </p>
								<input type="text" value={lastBook} onChange={event => updateLastBook(event, i)} />
							</div>
						);
					})}
				</div>
				<div className="image-preferences">
					<OptionList
						title="Number of books read per week"
						options={numberOfBooks}
						imageUrl={`/images/NumberOfBooksRead/${ageGroup}`}
						handleSelect={updateSinglePreference}
						type="numberOfBooksRead"
						selectedChild={selectedChild}
					/>
					<OptionList
						title="Reading level"
						options={readingLevels}
						imageUrl={`/images/ReadingLevels/${ageGroup}`}
						handleSelect={updateSinglePreference}
						type="readingLevel"
						selectedChild={selectedChild}
					/>
				</div>
			</div>
			<div className="preferences-multiple">
				<OptionList
					title="Child Preferences"
					options={categories}
					imageUrl={`/images/Preferences/${ageGroup}`}
					handleSelect={updateMultiplePreferences}
					type="categories"
					selectedChild={selectedChild}
				/>
				<OptionList
					title="Type of Books"
					options={types}
					imageUrl="/images/Type of Books"
					handleSelect={updateMultiplePreferences}
					type="types"
					selectedChild={selectedChild}
				/>
				<OptionList
					title="Authors"
					options={authors}
					imageFormat="jpg"
					imageUrl="/images/AllAuthors"
					showName={false}
					selectedChild={selectedChild}
					handleSelect={updateMultiplePreferences}
					type="authors"
				/>
				<OptionList
					title="Series"
					options={series}
					imageFormat="jpg"
					imageUrl="/images/AllSeries"
					showName={false}
					handleSelect={updateMultiplePreferences}
					type="series"
					selectedChild={selectedChild}
					hoverName={true}
				/>
			</div>
			<button
				className="gold-button"
				onClick={submitPreferences}
			>Submit</button>
		</div>
	);
};

export default Preferences;
