import { useEffect, useState } from 'react';
import './styles.scss';
import { FaStar } from 'react-icons/fa';
import BookSlider from '../BookSlider';
import Plans from '../Plans';
import { AiOutlineHeart } from 'react-icons/ai';
import { useParams } from 'react-router-dom';
import ReadMore from "react-read-more-read-less";
import { productDetails, /* reviews, */stats } from './constants';
import {setAlert} from '../../reducers/mainSlice';
import {useDispatch} from 'react-redux';
import {addToWishlist} from '../../reducers/wishlistSlice';
import axios from 'axios';
import urls from '../../utils/urls';
import randomInteger from 'random-int';
import devUrls from '../../utils/devUrls';

const Book = () => {
	const dispatch = useDispatch();
	const { guid } = useParams();
	const [ book, setBook ] = useState(null);
	const [similarBooks, setSimilarBooks] = useState([]);
	const [tagCount, setTagCount] = useState(0);

	const getBook = async () => {
		try {
			const response = await axios.get(urls.getBook, {params: {guid}});
			setBook({...response.data.book, ...response.data.details, rating: Number(response.data.book.rating) === 5 ? 5 : 4});
			setTagCount(1 + response.data.book.categories.length);
		} catch(err) {}
	};

	const getSimilarBooks = async () => {
		const response = await axios.post(urls.getSimilarBooks, {age_group: randomInteger(1, 5)});
		setSimilarBooks(response.data.data);
	};

	const wishlistAdd = async (book) => {
		dispatch(setAlert({ text: `${book.name} added to wishlist`, color: '#33A200' }));
		dispatch(addToWishlist({ book }));
		try {
			await axios.post(devUrls.addToWishlist, {guid: book.guid}, {withCredentials: true});
		} catch (err) {
			console.log(err);
		}
	};

	useEffect(() => {
		if(book)
			getSimilarBooks();
	}, [book]);

	useEffect(() => {
		getBook();
	}, [guid]);

	if (!book) return <h1 style={{ margin: '10rem', textAlign: 'center', padding: '1rem' }}>Loading...</h1>;
	return (
		<div className="single-book">
			<div className="book">
				<div className="book-main-details">
					<h1>{book.name}</h1>
					<div className="rating">
						{Array(5).fill(true).map((rating, i) => {
							if (i > Number(book.rating - 1 || -1)) return <FaStar style={{ color: '#F2F2F2' }} key={i}/>;
							return <FaStar style={{ color: '#DFB300' }} />;
						})}
						<p>{book.rating} Stars</p>
					</div>
					<p>
						Author - {book.authors?.map((author, i) => {
							if (i > 0) return <b key={author}>, {author}</b>;
							return <b key={author}>{author}</b>;
						})}
					</p>
					<p>Publisher - <b>{book.publisher}</b></p>
					<div className="stats">
						{stats.map((stat, i) => {
							return (
								<div className='stat'>
								<img alt="Stat" src={stat.image} />
								<p>{stat.name ? `${book[stat.name]} ${stat.pad || ''}` : stat.default}</p>
								</div>
							);
						})}
						<a href="#product-details">See More Details</a>
					</div>
					<div className="description">
						<h3>Description</h3>
						<ReadMore
							readMoreText={<b>Read More</b>}
							readLessText={<b>Read Less</b>}
							charLimit={150}
						>
							{book.description}
						</ReadMore>
					</div>
				</div>
				<div className="book-image">
					<div className="image">
						<img alt="Book" src={book.image}/>
						{tagCount > 0 &&
							<div className="tags">
							<a title={book.book_format}>
								<img alt='Tag' src={`/images/Tags/${book.book_format}.png`} onError={(event) => {
									setTagCount(_ => _ - 1);
									event.target.style.display = 'none'
								}}/>
							</a>
							{book.categories?.map(category => {
								return (
									<a title={category} key={category}>
										<img alt='Tag' src={`/images/Tags/${category}.png`} onError={(event) => {
											setTagCount(_ => _ - 1);
											event.target.style.display = 'none'
											event.target.parentNode.style.display = 'none';
										}}/>
									</a>
								);
							})}
						</div>}
					</div>
					<button onClick={wishlistAdd}>
						<AiOutlineHeart />
						<p>Add to Wishlist</p>
					</button>
				</div>
			</div>
			<BookSlider books={similarBooks} booksTitle="More like this" bookBackground="#F1F1F0" />
			<div className="product-details" id="product-details">
				<h3>Product Details</h3>
				<div className="product-detail-list">
					{productDetails.map((detail, i) => {
						return (
							<div className="product-detail" key={i}>
								<b>{detail.title}</b>
								{detail.name
									? typeof book[detail.name] === typeof []
											? <div className="detail-list" style={detail.name === 'authors' ? {margin: 'auto'} : {}}>
													{book[detail.name].map((d, j) => {
														if(detail.name === 'authors') {
															if(j < book[detail.name].length - 1)
																return d + ', ';
															return d;
														}
														if (j > 0) return <p key={j} className="highlighted-detail">{d}</p>;
														return <p className="highlighted-detail">{d}</p>;
													})}
												</div>
											: <p>{book[detail.name]}</p>
									: <p>{detail.default}</p>}
							</div>
						);
					})}
				</div>
			</div>

			<Plans />
		</div>
	);
};

export default Book;


