import { useEffect, Fragment } from 'react';
import './styles.scss';
import { useDispatch, useSelector } from 'react-redux';
import { setAlert, flushRegisterDetails } from '../../reducers/mainSlice';
import { registerFlow } from './constants';
import { useNavigate } from 'react-router-dom';

const Register = () => {
	const navigate = useNavigate();
	const dispatch = useDispatch();
	const { main: { registrationStep, registerDetails: { registrationDone, paymentDone, mobileNumber } } } = useSelector(
		state => state
	);

	const finishRegistration = async path => {
		dispatch(flushRegisterDetails());
		try {
			navigate(path);
			dispatch(setAlert({ text: 'Registered successfully', color: '#33A200' }));
		} catch (err) {
			console.log(err);
		}
	};

	useEffect(
		() => {
			if (!mobileNumber) navigate('/');
		},
		[]
	);

	return (
		<Fragment>
			{registrationDone &&
				<div className="payment-done">
					<h1>Congratulations!!!</h1>
					<b>Registration Complete</b>
					<img src="/images/Art/congratulations.png" alt="Congratulations" />
					<p>Dive into the creative world of Books</p>
					<button className="gold-button" onClick={() => finishRegistration('/preferences')}>Add Preferences</button>
					<button className="gold-border-button" onClick={() => finishRegistration('/')}>Maybe Later</button>
				</div>}
			{paymentDone &&
				<div className="payment-done">
					<h1>Congratulations!!!</h1>
					<b>Your payment was successful</b>
					<img src="/images/Art/congratulations.png" alt="Congratulations" />
					<p>Thank you for subscribing</p>
					<h5>We will contact you through WhatsApp on your registered mobile number</h5>
					<button className="gold-button" onClick={() => finishRegistration('/')}>Next</button>
				</div>}
			<div className="register" style={paymentDone || registrationDone ? { opacity: '0.1' } : {}}>
				<div className="progress-bar">
					{registerFlow.map((step, i) => {
						const style = i <= registrationStep ? { backgroundColor: '#343434' } : { backgroundColor: '#B0AFAF' };
						return (
							<div key={i} className="step">
								{i > 0 && <div className="line" style={style} />}
								<div className="step-title">
									<div className="circle" style={style} />
									<p>{step.title}</p>
								</div>
							</div>
						);
					})}
				</div>
				{registerFlow[registrationStep].element}
			</div>
		</Fragment>
	);
};

export default Register;
